dict.string:
#!/usr/bin/env ruby
def_col = "" if ENV["TM_SELECTED_TEXT"] =~ /#?([0-9A-Fa-f]{6})/ then def_col += " default color {"; def_col += $1.scan(/../).map { |i| 257*eval("0x" + i) }.join(",") def_col += "}" end
col = %x{ osascript 2>/dev/null \ -e 'tell app "SystemUIServer"' \ -e 'activate' \ -e 'choose color#{def_col}' \ -e 'end tell' }
%x{ osascript -e 'tell app "TextMate" to activate' &>/dev/null &} exit 200 if col == ""
col = col.split(",").map { |i| i.to_i / 257 } printf '#%02X%02X%02X', *col
